/* This file will support the component for the homepage. It'll include a navbar, an image, and an About Me. */


import { StyleSheet, View, Image, Text } from "react-native";
import TopBar from "./TopBar";

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "aliceblue",
        alignItems: "center",
        justifyContent: "center",
    },
    image: {
        width: 500,
        height: 500,
        borderRadius: 500,
        overflow: "hidden",
        borderWidth: 3,
        borderColor: "black"
        //alignItems: "center",
        //justifyContent: "center",
    },
    name: {
        fontFamily: "monospace",
        fontSize: 50,
        color: "black",
    },
    description: {
        fontFamily: "monospace",
        fontSize: 16,
        color: "black"
    }
})
function Home() {
    return (
        <View style={styles.container}>
            <TopBar></TopBar>
            <Image
                style={styles.image}
                source={{
                    uri: 'https://miro.medium.com/max/600/1*nQOh17AII63UbEakSAWICQ.png',
                }}
            /> 
            <Text style={styles.name}> Zayaan Rahman </Text>
        </View>
    );
}

export default Home;
