/* This file is used for the navigation bar at the top. */

import React from 'react';
import { StyleSheet, View, TouchableOpacity, Text, Dimensions } from "react-native";

import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
  
const windowWidth = Dimensions.get("window").width;
  
const styles = StyleSheet.create({
  // TODO - First and last button too close to edge
  container: {
    alignSelf: "stretch",
    width: windowWidth,
    height: 65,
    backgroundColor: "white",
    color: "red",
    alignItems: "center",
    justifyContent: "space-between",
    flexDirection: "row",
    borderWidth: 1,
    borderBottomColor: "black"
  },
  button: {
    alignItems: "center",
    justifyContent: "center",
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 20,
    backgroundColor: "white",
    //borderWidth: 1,
    //borderStyle: "solid",
    //borderColor: "black",
  },
  text: {
    fontSize: 35,
    fontWeight: "bold",
    color: "lightskyblue",
    fontFamily: "Iceland"
  }
});
  
const bounce = {
  y: {
    duration: 0.4,
    yoyo: Infinity,
    ease: "easeOut"
  }
}

function TopBar() {
  let nav = useNavigate();

  return (
    <div style={{
      width: "2rem",
      height: "2rem",
      display: "flex",
      justifyContent: "space-around"
    }}
    >
      <motion.span
      style={styles.container}
      transition={bounce}
      animate={{
        y: ["100%", "-100%"]
      }}/>
    <View style={styles.container}>
      <TouchableOpacity onPress={nav("/home")}>
        <View style={styles.button}>
          <Text style={styles.text}>HOME</Text>
        </View>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => {nav("/courses")}}>
        <View style={styles.button}>
          <Text style={styles.text}>COURSES</Text>
        </View>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => {nav("/skills")}}>
        <View style={styles.button}>
          <Text style={styles.text}>SKILLS</Text>
        </View>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => {nav("/education")}}>
        <View style={styles.button}>
          <Text style={styles.text}>EDUCATION</Text>
         </View>
       </TouchableOpacity>
       <TouchableOpacity onPress={() => {nav("/projects")}}>
        <View style={styles.button}>
          <Text style={styles.text}>PROJECTS</Text>
         </View>
       </TouchableOpacity>
    </View>
    </div>
  );
}
  
export default TopBar;
