const styles = StyleSheet.create({
    box: {
        width: 150,
        height: 150,
        backgroundColor: "purple",
        borderRadius: 30
    },
    circle: {
        width: 100,
        height: 100,
        borderRadius: 100 / 2,
        backgroundColor: "red",
    }
    
})
