//           pathLength: { delay, type: "spring", duration: 1.5, bounce: 0 },
//           opacity: { delay, duration: 0.01 }
//         }
//       };
//     }
//   };


let { x, y, width, height} = document.querySelector("body").getBoundingClientRect();
let cubeSize = 500;
function generateRandomLocation() {
  return {
    x: Math.floor(Math.random() * (width - cubeSize)),
    y: Math.floor(Math.random() * (height - cubeSize))
  };
}

/* Generate components */
function generateComps() {
    let components = []
    for (let i = 1; i <= 10; i++) {
        let coords = generateRandomLocation();
        console.log(coords)
        components.push(<View style={{width: coords.x, height: coords.y, backgroundColor: "purple", borderRadius: 30}}></View>)
    }
    return components
}

//letters are 2 boxes wide, 3 boxes tall
function Square() {
    return(
        <motion.div animate={{scale: [1, 2, 2, 1, 1], rotate: [0, 0, 270, 270, 0], borderRadius: ["20%", "20%", "50%", "50%", "20%"]}}   
            transition={{
            duration: 2,
            ease: "easeInOut",
            times: [0, 0.2, 0.5, 0.8, 1],
            repeat: Infinity,
            repeatDelay: 1
        }}>
            {/* TODO - how to render a list of components */}
            <View>{generateComps()}</View>
        </motion.div>
    // <motion.svg width="500" height="600" viewBox="0 0 600 600" initial="hidden" animate="visible">
    //     <motion.circle cx="100" cy="100" r="80" fill="none" stroke="red" variants={draw} custom={1}></motion.circle>
    // </motion.svg>
    )
}

export default Square;
