/* This file will support the component for the homepage. It'll include a navbar, an image, and an About Me. */

import { StyleSheet, View, Image, Text, Animated } from "react-native";
import React, { useRef } from 'react';

import { motion, AnimationProps } from "framer-motion";

import TopBar from "./TopBar";
import Links from "./Links";

/* https://reactnative.dev/docs/animations */
const Fader = (props) => {
    const fadeAnim = useRef(new Animated.Value(0)).current
    React.useEffect(() => {
        Animated.timing(
            fadeAnim,
            {
                toValue: 1,
                duration: 2500,
            }
        ).start();
    }, [fadeAnim])
    return (
        <Animated.View
          style={{
                ...props.style,
              opacity: fadeAnim,
              alignItems: "center",
              justifyContent: "center",
         }}
     >
            {props.children}
        </Animated.View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "aliceblue",
        alignItems: "center",
        justifyContent: "center",
    },
    image: {
        width: 300,
        height: 300,
        borderRadius: 500,
        overflow: "hidden",
        borderWidth: 3,
        borderColor: "black"
    },
    name: {
        fontFamily: "Iceland",
        fontSize: 50,
        color: "black",
    },
    description: {
        fontFamily: "Iceland",
        fontSize: 20,
        color: "black",
        padding: 10
    },
})

const mainPathVariant = {
        transition: {
        duration: 4,
        ease: "easeInOut",
    },
    variants: {
        visible: {
            pathLength: 1,
    },
    hidden: {
        pathLength: 0,
        },
    },
};

const pathColor = "black";

function Home() {
