/* This file will support the component for the homepage. It'll include a navbar, an image, and an About Me. */

import { StyleSheet, View, Image, Text, Animated } from "react-native";
import React, { useRef } from 'react';

import { motion } from "framer-motion";
import ds from "../svg/ds";

import TopBar from "./TopBar";
import Links from "./Links";

/* https://reactnative.dev/docs/animations */
const Fader = (props) => {
    const fadeAnim = useRef(new Animated.Value(0)).current
    React.useEffect(() => {
        Animated.timing(
            fadeAnim,
            {
                toValue: 1,
                duration: 2500,
            }
        ).start();
    }, [fadeAnim])
    return (
        <Animated.View
          style={{
                ...props.style,
              opacity: fadeAnim,
              alignItems: "center",
              justifyContent: "center",
         }}
     >
            {props.children}
        </Animated.View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "aliceblue",
        alignItems: "center",
        justifyContent: "center",
    },
    image: {
        width: 300,
        height: 300,
        borderRadius: 500,
        overflow: "hidden",
        borderWidth: 3,
        borderColor: "black"
    },
    name: {
        fontFamily: "Iceland",
        fontSize: 50,
        color: "black",
    },
    description: {
        fontFamily: "Iceland",
        fontSize: 20,
        color: "black",
        padding: 10
    },
})

const svgV = {
    hidden: { rotate: -180 },
    visible: {
        rotate: 0,
        transition: { duration : 1 }
    }
}

function Home() {
    return (
        <View style={styles.container}>
            <motion.svg variants={svgV} initial="hidden" animate="visible">
                <path backgroundColor="white" fill="none" d="M40 40 L80 40 C80 40 80 80 40 80 C40 80 0 80 0 40 C0 C0 40 00 40 0Z"/>
                <Text style={styles.name}> EQWEQEWQEW </Text>
            </motion.svg>
        </View>
