import React, { useRef, useEffect } from 'react';
import { Animated, Text, View } from 'react-native';

const Fader = (props) => {
    const fadeAnim = useRef(new Animated.Value(0)).current //opacity is initially 0

    React.useEffect(() => {
        Animated.timing(
            fadeAnim,
            {
            toValue: 1,
            duration: 10000,
            }
        ).start();
    }, [fadeAnim])

    return (
        <Animated.View
          style={{
                ...props.style,
              opacity: fadeAnim,
         }}
     >
            {props.children}
        </Animated.View>
    );
}
